void SelectionSort(int a[],int n,int (*compare)(int a,int b))
{ int i,j,k,temp; for(i=0;i<n-1;i++) { k=i; for(j=i+1;j<n;j++) { if((compare)(a[j],a[k]))//调用函数指针compare指向的函数 k=j; } if(k!=i) { temp=a[k]; a[k]=a[i]; a[i]=temp; } }
